<script setup>
import { onMounted, onBeforeUnmount, ref, watch, useTemplateRef } from 'vue'
import { gsap } from 'gsap'
import { MorphSVGPlugin } from 'gsap/all'

gsap.registerPlugin(MorphSVGPlugin)

const play = ref(false)
const neutral = useTemplateRef('mouth-neutral')
const smile = useTemplateRef('mouth-smile')

let tween

onMounted(() => {
  gsap.set(smile.value, { visibility: 'hidden' })

  tween = gsap.to(neutral.value, {
    morphSVG: smile.value,
    duration: 0.5,
    yoyo: true,
    repeat: 1,
    repeatDelay: 2,
    paused: true,
  })

  play.value = true

  watch(
    play,
    (val) => {
      if (val) tween.play(0)
      else tween.pause(0)
    },
    { immediate: true },
  )
})

onBeforeUnmount(() => {
  tween?.kill()
})
</script>

<style scoped>
.eyebrow {
  transform-origin: center;
  transform-box: fill-box;
}

.play-left {
  animation: brow-left 3s 1 both;
}

.play-right {
  animation: brow-right 3s 1 both;
}

@keyframes brow-left {
  0% {
    transform: translateY(0) rotate(0);
  }

  12%,
  88% {
    transform: translateY(-2px) rotate(-10deg);
  }

  100% {
    transform: translateY(0) rotate(0);
  }
}

@keyframes brow-right {
  0% {
    transform: translateY(0) rotate(0);
  }

  12%,
  88% {
    transform: translateY(-2px) rotate(6deg);
  }

  100% {
    transform: translateY(0) rotate(0);
  }
}
</style>

